const api_url="https://api.jsonbin.io/b/5e1aa891b236b871b3605dd6/1";var products=document.getElementById("products");function getJson(){fetch(api_url).then(a=>a.json()).then(a=>{getProducts(a)})}function formatPrice(a){return a=a/100}function bestSeller(a){return a=a.includes("Brazil Cachoeira")}function getProducts(a){a.data.sort(function(a,n){return a.name.localeCompare(n.name)});const n=a.data.length;for(var t=0;t<n;t++){document.getElementsByClassName("product");bestSeller(a.data[t].name)?products.innerHTML+=`\n                <figure class="product">\n                    <span class="best-seller tada">\n                        BEST SELLER\n                    </span>\n                    <a href="#" id="${a.data[t].id}">\n                        <img src="${a.data[t].image}" srcset="${a.data[t].image}" 1280w alt="${a.data[t].name}" size="(max-width: 1280px) 100vw">\n                        <figcaption>\n                            <p>${a.data[t].name}</p>\n                            <span>\n                                ${formatPrice(a.data[t].price)}\n                            </span>\n                        </figcaption>\n                    </a>\n                </figure>\n            `:products.innerHTML+=`\n                <figure class="product">\n                    <a href="#" id="${a.data[t].id}">\n                        <img src="${a.data[t].image}" srcset="${a.data[t].image}" 1280w alt="${a.data[t].name}" size="(max-width: 1280px) 100vw">\n                        <figcaption>\n                            <p>${a.data[t].name}</p>\n                            <span>\n                                $ ${formatPrice(a.data[t].price)}\n                            </span>\n                        </figcaption>\n                    </a>\n                </figure>\n            `}}getJson(),setTimeout(function(){document.getElementById("comment").classList.add("visible")},3e3);